import React, { useState } from 'react';

const MyForm = () => {
  const [formData, setFormData] = useState({
    firstName: '',
    tel: '',
    email: '',
  });

  const handleChange = (e) => {
    const { name, value } = e.target;
    setFormData({
      ...formData,
      [name]: value,
    });
  };

  // Обработчик отправки формы
  const handleSubmit = (e) => {
    e.preventDefault();
    // Вы можете выполнить здесь дополнительные действия, такие как отправка данных на сервер
    console.log('Form submitted:', formData);
  };

  return (
    <div className="form-background">
    <div className="form-content">
      <div className="form-wrapper">
        <h1>Поможем в выборе!</h1>
        <form onSubmit={handleSubmit}>
          <label htmlFor="name">Имя</label>
          <input type="text" id="name" name="firstname" value={formData.firstName} onChange={handleChange} placeholder="Введите ваше имя" required />

          <label htmlFor="email">Email:</label>
          <input type="email" id="email" name="email" value={formData.email} onChange={handleChange} placeholder="Введите ваш email" required />

          <label htmlFor="tel">Телефон</label>
          <input type="tel" id="tel" name="tel" value={formData.tel} onChange={handleChange} placeholder="Введите ваш телефон" required />

          <input type="checkbox" name="agree" required /><label htmlFor="agree">Я принимаю условия обработки данных</label>
          <button type='submit'>Отправить</button>
        </form>
      </div>
      <div className="form-info-wrapper">
        <div className="form-info"></div>
      </div>
    </div>
  </div>
  );
};

export default MyForm;
