import React, { useMemo, useCallback } from 'react';
import Button from '../components/button';

const Main = () => {
  // Пример использования useMemo для мемоизации преобразования данных
  const processedData = useMemo(() => {
    // Логика обработки данных
    return /*...*/;
  }, []); // Пересчитывать только при монтировании компонента

  // Пример использования useCallback для мемоизации колбэк-функции
  const handleButtonClick = useCallback(() => {
    // Логика обработки клика
  }, []); // Пересоздавать только при монтировании компонента

  return (
    <div>
      {/* Остальной код компонента */}
      <div className="content-main-block-container1">
        <div>
          <h1 className="content-main-block-container1-h1">BrainlyAcademy</h1>
          {/* Используем Button с мемоизированным колбэком */}
          <Button text="fdfd" onClick={handleButtonClick} />
        </div>
        <div>
          <ul>
            <li><span className="checkmark">&#10004;</span>№100000 По качеству обучения по версии Smart Ranking</li>
            <li><span className="checkmark">&#10004;</span>90 различных курсов</li>
            <li><span className="checkmark">&#10004;</span>Учитесь с любым графиком из любой точки мира</li>
          </ul>
        </div>
        <div className="button-container">
          <button className="btn" onClick={handleButtonClick}>
            Оставить заявку
          </button>
        </div>
      </div>
    </div>
  );
};

export default Main;
